pragma solidity 0.8.13;

import {
    IArrakisV2Beacon
} from "@arrakisfi/v2-core/contracts/interfaces/IArrakisV2Beacon.sol";
import {
    OwnableUpgradeable
} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {
    EnumerableSet
} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

/// @title Arrakis Factory Storage Smart Contract
// solhint-disable-next-line max-states-count
abstract contract ArrakisV2GaugeFactoryStorage is OwnableUpgradeable {
    using EnumerableSet for EnumerableSet.AddressSet;

    IArrakisV2Beacon public immutable arrakisGaugeBeacon;
    EnumerableSet.AddressSet internal _gauges;
    address public defaultRewardToken;
    address public ve;
    address public veBoost;

    event InitFactory(address owner);
    event GaugeCreated(address deployer, address gauge);
    event DefaultRewardSet(address token, address ve, address veBoost);

    // #region constructor.

    constructor(address gaugeBeacon_) {
        require(gaugeBeacon_ != address(0), "Z");
        arrakisGaugeBeacon = IArrakisV2Beacon(gaugeBeacon_);
    }

    // #endregion constructor.

    function initialize(
        address owner_,
        address rewardToken_,
        address ve_,
        address veBoost_
    ) external initializer {
        require(
            owner_ != address(0) &&
                rewardToken_ != address(0) &&
                ve_ != address(0) &&
                veBoost_ != address(0),
            "address zero"
        );
        _transferOwnership(owner_);
        defaultRewardToken = rewardToken_;
        ve = ve_;
        veBoost = veBoost_;
        emit DefaultRewardSet(rewardToken_, ve_, veBoost_);
        emit InitFactory(owner_);
    }

    function setDefaultReward(
        address rewardToken_,
        address ve_,
        address veBoost_
    ) external onlyOwner {
        require(
            rewardToken_ != address(0) &&
                ve_ != address(0) &&
                veBoost_ != address(0),
            "address zero"
        );
        defaultRewardToken = rewardToken_;
        ve = ve_;
        veBoost = veBoost_;
        emit DefaultRewardSet(rewardToken_, ve_, veBoost_);
    }

    // #endregion admin set functions

    // #region admin view call.

    /// @notice get gauge instance admin
    /// @param proxy instance of Arrakis V2.
    /// @return admin address of Arrakis V2 instance admin.
    function getProxyAdmin(address proxy) external view returns (address) {
        // We need to manually run the static call since the getter cannot be flagged as view
        // bytes4(keccak256("admin()")) == 0xf851a440
        (bool success, bytes memory returndata) = proxy.staticcall(
            hex"f851a440"
        );
        require(success, "PA");
        return abi.decode(returndata, (address));
    }

    /// @notice get gauge implementation
    /// @param proxy instance of Arrakis V2.
    /// @return implementation address of Arrakis V2 implementation.
    function getProxyImplementation(address proxy)
        external
        view
        returns (address)
    {
        // We need to manually run the static call since the getter cannot be flagged as view
        // bytes4(keccak256("implementation()")) == 0x5c60da1b
        (bool success, bytes memory returndata) = proxy.staticcall(
            hex"5c60da1b"
        );
        require(success, "PI");
        return abi.decode(returndata, (address));
    }

    // #endregion admin view call.
}
","keccak256":"0xb71e2fb66fa2b6ef2fe7b71b67b1a4d136057f129e413106f4cdc88f205c5335","license":"UNLICENSED"},"contracts/interfaces/IGauge.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
// solhint-disable-next-line compiler-version
pragma solidity >=0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

// solhint-disable func-name-mixedcase
interface IGauge is IERC20 {
    // solhint-disable var-name-mixedcase
    struct Reward {
        address distributor;
        uint256 period_finish;
        uint256 rate;
        uint256 last_update;
        uint256 integral;
    }

    function initialize(
        address stakingToken,
        address admin,
        address reward,
        address ve,
        address veBoost,
        address distributor
    ) external;

    function deposit(uint256 value, address addr) external;

    function withdraw(uint256 value) external;

    function add_reward(address token, address distributor) external;

    function set_reward_distributor(address token, address distributor)
        external;

    function user_checkpoint(address addr) external returns (bool);

    function claim_rewards(address addr) external;

    function deposit_reward_token(address, uint256) external;

    function commit_transfer_ownership(address) external;

    function accept_transfer_ownership() external;

    function admin() external view returns (address);

    function future_admin() external view returns (address);

    function claimable_reward(address addr, address token)
        external
        view
        returns (uint256);

    function claimed_reward(address addr, address token)
        external
        view
        returns (uint256);

    function reward_count() external view returns (uint256);

    function reward_tokens(uint256 index) external view returns (address);

    function reward_data(address) external view returns (Reward memory);

    function staking_token() external view returns (address);

    function name() external view returns (string memory);

    function symbol() external view returns (string memory);

    function decimals() external view returns (uint8);
}
